#2 USER GENERATED BY USER
import random
import string
def userByUser(nu,car):
    numeros=''.join(random.choices(string.digits, k=nu))
    letras=''.join(random.choices(string.ascii_letters, k=car))
    idFinal=numeros+letras
    return''.join(random.sample(idFinal,len(idFinal)))
car=int(input('Type the number of the characters you want: '))
nu=int(input('Type the number of the numbers you want: '))
idGenerado=userByUser(nu,car)
print(f'ID generado: {idGenerado}')

